import { useState } from "react";
import "../Style_Pages/Update_File.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import React from "react";
import axios from "axios";
const ProfileUpdateForm = () => {
  const [update, setUpdates] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
  });
  const { id } = JSON.parse(localStorage.getItem("user"));
  // const { status } = JSON.parse(localStorage.getItem("user"));
  const handleSubmit = (e) => {
    e.preventDefault();
    setUpdates({ ...update, err: [] });
    axios
      .put(
        "http://localhost:4000/users",
        {
          Name: update.name,
          Email: update.email,
          Password: update.password,
          Phone: update.phone,
          // Status: status,
        },
        {
          headers: {
            id: id,
          },
        }
      )
      .then((response) => {
        console.log(response.data);
        // handle success
        setUpdates({ ...update, err: [], text: "" });
        alert(response.data);
      })
      .catch((error) => {
        console.log(error.response.data);
        // handle error
        setUpdates({
          ...update,
          // loading: false,
          err: error.response.data.message,
        });
      });
  };
  return (
    <>
      <Header />

      <div className="form-container">
        <h1 className="form-title">Update Profile</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label className="form-label">Name</label>
            <input
              className="form-input"
              type="text"
              name="Name"
              value={update.name}
              onChange={(e) => setUpdates({ ...update, name: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              className="form-input"
              type="text"
              name="Phone"
              value={update.phone}
              onChange={(e) => setUpdates({ ...update, phone: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="Email"
              name="Email"
              value={update.email}
              onChange={(e) => setUpdates({ ...update, email: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="Password"
              name="Password"
              value={update.password}
              onChange={(e) =>
                setUpdates({ ...update, password: e.target.value })
              }
            />
          </div>
          <button
            type="submit"
            // disabled={update.loading === true}
            className="btn "
            style={{ backgroundColor: "rgb(55, 2, 104)", color: "white" }}
          >
            {" "}
            Save Changes
          </button>
        </form>
      </div>

      <Footer />
    </>
  );
};

export default ProfileUpdateForm;
