import { useState } from "react";
import "../Style_Pages/Update_File.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import React from "react";
import axios from "axios";
const ProfileUpdateForm = () => {
  const [update, setUpdates] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    loading: false,
    err: [],
    text: "",
  });
  const { id } = JSON.parse(localStorage.getItem("user"));
  // const { status } = JSON.parse(localStorage.getItem("user"));
  const handleSubmit = (e) => {
    e.preventDefault();
    setUpdates({ ...update, loading: true, err: [] });
    axios
      .put(
        "http://localhost:4000/users",
        {
          Name: update.name,
          Email: update.email,
          Password: update.password,
          Phone: update.phone,
          // Status: status,
        },
        {
          headers: {
            id: id,
          },
        }
      )
      .then((response) => {
        console.log(response.data);
        // handle success
        setUpdates({ ...update, loading: false, err: [], text: "" });
        alert(response.data.message);
      })
      .catch((error) => {
        console.log(error.response.data);
        // handle error
      });
  };
  return (
    <>
      <Header />

      <div className="form-container">
        <h1 className="form-title">Update Profile</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label className="form-label">First Name</label>
            <input
              className="form-input"
              type="text"
              name="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              className="form-input"
              type="text"
              name="Phone"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="Email"
              name="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="Password"
              name="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">Save Changes</button>
        </form>
      </div>

      <Footer />
    </>
  );
};

export default ProfileUpdateForm;
