import React, { useState, useRef, useEffect } from "react";

import "../Style_Pages/Add.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import { useParams } from "react-router-dom";
import { getAuthUser } from "../helper/storage";

export const EditQuestion = () => {
  let { id } = useParams();
  const auth = getAuthUser();
  const [question, setQuestion] = useState({
  
    loading: false,
    reload: false,
    success: null,
  });
  const image = useRef(null);

  const updateMovie = (e) => {
    e.preventDefault();

    setMovie({ ...movie, loading: true });

    const formData = new FormData();
    formData.append("name", movie.name);
    formData.append("description", movie.description);
    if (image.current.files && image.current.files[0]) {
      formData.append("image", image.current.files[0]);
    }
    axios
      .put("http://localhost:4000/movies/" + id, formData, {
        headers: {
          token: auth.token,
          "Content-Type": "multipart/form-data",
        },
      })
      .then((resp) => {
        setMovie({
          ...movie,
          loading: false,
          success: "movie updated successfully !",
          reload: movie.reload + 1,
        });
      })
      .catch((err) => {
        setMovie({
          ...movie,
          loading: false,
          success: null,
          err: "Something went wrong, please try again later !",
        });
      });
  };

  useEffect(() => {
    axios
      .get("http://localhost:4000/movies/" + id)
      .then((resp) => {
        setMovie({
          ...movie,
          name: resp.data.name,
          description: resp.data.description,
          image_url: resp.data.image_url,
        });
      })
      .catch((err) => {
        setMovie({
          ...movie,
          loading: false,
          success: null,
          err: "Something went wrong, please try again later !",
        });
      });
  }, [movie.reload]);

  
  return (
    <>
      <Header />
   <div className="form">
     <h1 className="title">Edit Questions and Answers</h1>
     <form  >
         
       <div>
         <label className="label"> Audio</label>
         <input className="input" type="Text"   />
       </div>

       <div className="input-quistion">
         <label className="label"> Question</label>
         <input type="text" name=" Question"    />

         <label className="label" >answer1</label>
         <input className="inputAns" type="text"    />


         <label className="label">answer2</label>
         <input className="inputAns"type="text"  />

        

         <label className="label" >answer3</label>
         <input className="inputAns" type="text"   />

        

         <label className="label">answer4</label>
         <input className="inputAns"type="text"  />

       
           <label className="label"> Correct Ans</label>
         <input type="text" name=" Question"   />
      
       </div>

       <button style={{marginLeft:400}} type="submit" >Update</button>
     </form>
   </div>

   <Footer />
    </>
  
);
};
