import { useState } from "react";
import "../Style_Pages/Update_File.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import React from "react";
import axios from "axios";
const ProfileUpdateForm = () => {
  const [update, setUpdates] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    loading: false,
    err: [],
    text: "",
  });
  const { id } = JSON.parse(localStorage.getItem("user"));
  // const { status } = JSON.parse(localStorage.getItem("user"));
  const handleSubmit = (e) => {
    e.preventDefault();
    setUpdates({ ...update, loading: true, err: [] });
    axios
      .put(
        "http://localhost:4000/users",
        {
          Name: update.name,
          Email: update.email,
          Password: update.password,
          Phone: update.phone,
          // Status: status,
        },
        {
          headers: {
            id: id,
          },
        }
      )
      .then((response) => {
        console.log(response.data);
        // handle success
        setUpdates({ ...update, loading: false, err: [], text: "" });
        alert(response.data);
      })
      .catch((error) => {
        console.log(error.response.data);
        // handle error
        setUpdates({
          ...update,
          loading: false,
          err: error.response.data.errors,
        });
      });
  };
  return (
    <>
      <Header />

      <div className="form-container">
        <h1 className="form-title">Update Profile</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label className="form-label">First Name</label>
            <input
              className="form-input"
              type="text"
              name="Name"
              value={update.name}
              onChange={(e) => setUpdates({ ...update, name: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              className="form-input"
              type="text"
              name="Phone"
              value={update.name}
              onChange={(e) => setUpdates({ ...update, name: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="Email"
              name="Email"
              value={update.name}
              onChange={(e) => setUpdates({ ...update, name: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="Password"
              name="Password"
              value={update.name}
              onChange={(e) => setUpdates({ ...update, name: e.target.value })}
            />
          </div>
          <button type="submit" disabled={update.loading === true}>
            Save Changes
          </button>
          {update.err.map((error, index) => (
            <Alert
              key={index}
              style={{
                color: "white",
                width: 350,
                backgroundColor: "#690650",
                height: 80,
                borderRadius: 10,
              }}
            >
              {error.msg}
            </Alert>
          ))}
        </form>
      </div>

      <Footer />
    </>
  );
};

export default ProfileUpdateForm;
