import { useState } from "react";
import "../Style_Pages/Update_File.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import { getAuthUser } from "../helper/storage";
import React from "react";
import axios from "axios";
const UpdateProfilePage = () => {
  const [update, setRegister] = useState({
    email: "",
    password: "",
    name: "",
    phone: "",
    loading: false,
    err: [],
    text: "",
  });
  const updateProfile = (e) => {
    e.preventDefault();
    setRegister({ ...update, loading: true, err: [] });
    axios
      .put("http://localhost:4000/users", {
        Email: update.email,
        Password: update.password,
        Name: update.name,
        Phone: update.phone,
      })
      .then((resp) => {
        setRegister({ ...update, loading: false, err: [], text: "" });
        alert(resp.data.message);
      })
      .catch((errors) => {
        setRegister({
          ...update,
          loading: false,
          err: errors.update.data.errors,
        });
      });
  };

  // // Use state to store user data
  // const [user, setUser] = useState({
  //   Name: "",
  //   Email: "",
  //   Password: "",
  //   Phone: "",
  //   loading: false,
  //   err: "",
  // });
  // // Handle form submission
  // const updateProfile = (e) => {
  //   e.prevenDefault();
  //   setUser({ ...user, loading: true });
  //   const formData = new FormData();
  //   formData.append("Name", user.Name);
  //   formData.append("Email", user.Email);
  //   formData.append("Password", user.Password);
  //   formData.append("Phone", user.Phone);
  //   const { id } = JSON.parse(localStorage.getItem("user"));

  //   axios.put("http://localhost:4000/users/", formData, {
  //     headers: { id: id },
  //   });

  return (
    <>
      <Header />

      <div className="form-container">
        <h1 className="form-title">Update Profile</h1>
        <form>
          <div>
            <label className="form-label">Name</label>
            <input
              className="form-input"
              type="text"
              name="firstName"
              // value={user.Name}
              // onChange={(e) => setUser({ ...user, Name: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="email"
              name="email"
              // value={user.Email}
              // onChange={(e) => setUser({ ...user, Email: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              className="form-input"
              type="text"
              name="phone"
              // value={user.Phone}
              // onChange={(e) => setUser({ ...user, Phone: e.target.value })}
            />
          </div>
          <div>
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              // value={user.Password}
              // onChange={(e) => setUser({ ...user, Password: e.target.value })}
            />
          </div>
          <button type="submit">Save Changes</button>
        </form>
      </div>

      <Footer />
    </>
  );
  //   };
};
export default UpdateProfilePage;
