import React ,{useState,useEffect} from "react";

import "../Style_Pages/QuestionAdmin.css";

import axios from "axios";

import Header from "../Components/Header";
import Button from "../Components/Button";
import { getAuthUser } from "../helper/storage";
import { setQuestion } from "../helper/SetQuestion";
import Footer from "../Components/Footer";



export function QuestionsAdmin (){
  const auth = getAuthUser();
   const [currentQuestion,setCurrentQuestion]=useState(0);
   console.log(auth.email)
   const [exams, setExam] = useState({
     loading: true,
     results: [],
     err: null,
     reload: 0,
   });
 
   useEffect(() => {
     setExam({ ...exams, loading: true });
     axios
       .get("http://localhost:4000/quizzes/all")
       .then((resp) => {
         setExam({ ...exams, results: resp.data, loading: false, err: null });
         console.log(resp);
        
       })
       .catch((err) => {
         setExam({
           ...exams,
           loading: false,
           err: " something went wrong, please try again later ! ",
         });
       });
   }, [exams.reload]);
  const optionClicked = (isCorrect) => {
    //setQuestion( exams.results.data[currentQuestion]);

    if (currentQuestion + 1 < exams.results.length) {
      setCurrentQuestion(currentQuestion + 1);
     
      //  setQuestion( exams.results.data[currentQuestion]+1);
     
     } 
     
    }
 
    
 
 


    return(
      <>
      <Header />
{exams.loading==true&&(<h1>loading</h1>)}
      <div className="table_container">
        <table className="tableAdmin">
          <thead>
            
            <tr className="trAdmin">
              <th className="">Queation Id</th>
              <th>Audio</th>
              <th>Queation</th>
              <th>Ans_1</th>
              <th>Ans_2</th>
              <th>Ans_3</th>
              <th>Ans_4</th>
              <th>Correct</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
          
            {exams.loading===false&&exams.err==null&&(<>
             { exams.results.map((exam) => (
            <tr>
              <td>{exam.Id}</td>
              <td>{exam.Audio}%</td>
              <td>{exam.Question}</td>
              <td>{exam.Ans_1}</td>
              <td>{exam.Ans_2}</td>
              <td>{exam.Ans_3}</td>
              <td>{exam.Ans_4}</td>
              <td>{exam.Correct}</td>
              <td><Button name={"Edit"}/></td>
              <td><Button name={"Delete"}/></td>
            </tr>

          ))}
            </>)}
        
          </tbody>
        </table>
      </div>

      <Footer />
    </>

      );

}
