import React ,{useState} from "react";
import '../../Style_Pages/Quiz.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactAudioPlayer from 'react-audio-player';
import Header from "../Header";
import Button from "../Button";
import { getAuthUser } from "../../helper/storage";




export function Quizh (){
  const auth = getAuthUser();

    const [showFinalResults,setFinalResults]=useState(false);
   const [currentQuestion,setCurrentQuestion]=useState(0);
   const [score, setScore] = useState(0);
   console.log(auth.email)
   const [exams, setExam] = useState({
     loading: true,
     results: [],
     err: null,
     reload: 0,
   });
 
   useEffect(() => {
     setExams({ ...exams, loading: true });
     axios
       .get("http://localhost:4000/quizzes")
       .then((resp) => {
         setExam({ ...exams, results: resp.data, loading: false, err: null });
         console.log(resp);
       })
       .catch((err) => {
         setExam({
           ...exams,
           loading: false,
           err: " something went wrong, please try again later ! ",
         });
       });
   }, [exams.reload]);
  const optionClicked = (isCorrect) => {
    // Increment the score
    // if (isCorrect) {
    //   setScore(score + 1);
    // }

    if (currentQuestion + 1 < exams.results.length) {
      setCurrentQuestion(currentQuestion + 1);
    // } else {
    //     setFinalResults(true);
    // }
  };}

  /* Resets the game back to default */
  // const restartQuiz = () => {
  //   setScore(0);
  //   setCurrentQuestion(0);
  //   setFinalResults(false);
  // };


    return(
        <>
        <Header/>
        {exams.loading==true(<h1>loading</h1>)}
             <div className="quiz ">
        <h1 >Eary Test</h1>
        
   
    <div className="question-card ">
        <h2 >Question {currentQuestion +1} out of {questions.length}</h2><br/><br/>
                <ReactAudioPlayer
                    src={exams.results[currentQuestion].audio}
                    autoPlay
                    controls
                />
<br/><br/>
        <h3 className="question-text">{exams.results[currentQuestion].text}</h3>
                        <ul className="ul1">
                        {questions[currentQuestion].options.map((option) => {
              return (
                <li className="li1"
                  key={option.id}
                  onClick={() => optionClicked(option.isCorrect)}
                >
                  {option.text}
                </li>
              );

                            })}
        </ul>


{auth.status=== 1 && (
                  <>
                          <div className="btn1" style={{backgroundColor:"rgb(248, 243, 243)"}}>
        <Button name={'Edit'} go_to={'/edit'}/>
        <Button name={'Delete'} go_to={''}/>
        
</div>
          </>
                )}
     </div>
        
   
    </div>
        </>

    );

}
