import { useState } from "react";
import "../Style_Pages/Update_File.css";
import Header from "../Components/Header";
import Footer from "../Components/Footer";
import React, { Component } from "react";
import axios from "axios";
const UpdateProfilePage = () => {
  // Use state to store user data
  const [user, setUser] = useState({
    Name: "",
    Email: "",
    Password: "",
    Phone: "",
  });

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    setUser({ ...user, loading: true, err: [] });
    axios
      .put("http://localhost:4000/users", {
        Name: user.Name,
        Email: user.Email,
        Password: user.Password,
        Phone: user.Phone,
      })
      .then((response) => {
        setLogin({ ...user, loading: false, err: [] });
      });
    // Submit user data to API or server
    console.log(user);
  };

  // Handle form input changes
  const handleChange = (event) => {
    setUser({
      ...user,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <>
      <Header />

      <div className="form-container">
        <h1 className="form-title">Update Profile</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label className="form-label">Name</label>
            <input
              className="form-input"
              type="text"
              name="firstName"
              value={user.Name}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="email"
              name="email"
              value={user.Email}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              className="form-input"
              type="text"
              name="phone"
              value={user.Phone}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              value={user.Password}
              onChange={handleChange}
            />
          </div>
          <button type="submit">Save Changes</button>
        </form>
      </div>

      <Footer />
    </>
  );
};

export default UpdateProfilePage;
