import React ,{useState} from "react";
import '../../Style_Pages/Quiz.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactAudioPlayer from 'react-audio-player';
import Header from "../Header";
import Button from "../Button";
import { getAuthUser } from "../../helper/storage";




export function Quizh (){
  const auth = getAuthUser();

    const [showFinalResults,setFinalResults]=useState(false);
   const [currentQuestion,setCurrentQuestion]=useState(0);
   const [score, setScore] = useState(0);
   console.log(auth.email)
   const [exams, setE] = useState({
     loading: true,
     results: [],
     err: null,
     reload: 0,
   });
 
   useEffect(() => {
     setHistory({ ...histories, loading: true });
     axios
       .get("http://localhost:4000/quizzes")
       .then((resp) => {
         setHistory({ ...histories, results: resp.data, loading: false, err: null });
         console.log(resp);
       })
       .catch((err) => {
         setHistory({
           ...histories,
           loading: false,
           err: " something went wrong, please try again later ! ",
         });
       });
   }, [histories.reload]);
  const optionClicked = (isCorrect) => {
    // Increment the score
    if (isCorrect) {
      setScore(score + 1);
    }

    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
        setFinalResults(true);
    }
  };

  /* Resets the game back to default */
  const restartQuiz = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  };


    return(
        <>
        <Header/>
             <div className="quiz ">
        <h1 >Eary Test</h1>
        
        <h2 className="score">Score: {score}</h2>
        {showFinalResults ?(
    <div className="final-result">
        <h1 >Final Result</h1>
        <h2>
            {score} out of {questions.length} correct - (
            {(score / questions.length) * 100}%)
          </h2>
          <button className="btn1" onClick={() => restartQuiz()}>Restart Quiz</button>
    </div>

        ):(
    <div className="question-card ">
        <h2 >Question {currentQuestion +1} out of {questions.length}</h2><br/><br/>
                <ReactAudioPlayer
                    src={questions[currentQuestion].audio}
                    autoPlay
                    controls
                />
<br/><br/>
        <h3 className="question-text">{questions[currentQuestion].text}</h3>
                        <ul className="ul1">
                        {questions[currentQuestion].options.map((option) => {
              return (
                <li className="li1"
                  key={option.id}
                  onClick={() => optionClicked(option.isCorrect)}
                >
                  {option.text}
                </li>
              );

                            })}
        </ul>


{auth.status=== 1 && (
                  <>
                          <div className="btn1" style={{backgroundColor:"rgb(248, 243, 243)"}}>
        <Button name={'Edit'} go_to={'/edit'}/>
        <Button name={'Delete'} go_to={''}/>
        
</div>
          </>
                )}
     </div>
        
    )}
    </div>
        </>

    );

    
}
