import React ,{useState,useEffect} from "react";
import '../../Style_Pages/Quiz.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactAudioPlayer from 'react-audio-player';
import Header from "../Header";
import Button from "../Button";
import { getAuthUser } from "../../helper/storage";

import axios from "axios";


export function Quizh (){
  const auth = getAuthUser();
  const [currentQuestion,setCurrentQuestion]=useState(0);
  const len=0;
  //let {email}=auth.data.email;
  
console.log(auth.email)
  const [exams, setExam] = useState({
    loading: true,
    results: [],
    err: null,
    reload: 0,
  });

  useEffect(() => {
    setExam({ ...exams, loading: true });
    axios
      .get("http://localhost:4000/quizzes/")
      .then((resp) => {
        setExam({ ...exams, results: resp.data, loading: false, err: null }); 
        console.log(resp.data.length);
        len = resp.data.length;
        console.log(len) ;
      })
      .catch((err) => {
        setExam({
          ...exams,
          loading: false,
          err: " something went wrong, please try again later ! ",
        });
      });
  }, [exams.reload]);

    //const [showFinalResults,setFinalResults]=useState(false);
  // const [currentQuestion,setCurrentQuestion]=useState(0);
  // const [score, setScore] = useState(0);
   
 const optionClicked = () => {
    

    if (currentQuestion + 1 < exams.results.data['length']) {
     setCurrentQuestion(currentQuestion + 1);
   console.log(exams.results.data.length)
    }
  };

  /* Resets the game back to default */
  //const restartQuiz = () => {
  //  setScore(0);
  //  setCurrentQuestion(0);
  //  setFinalResults(false);
 // };


    return(
        <>
        <Header/>
             <div className="quiz ">
        <h1 >Eary Test</h1>
        

    <div className="question-card ">

        <h2 >Question {currentQuestion +1} out of {exams.length}</h2><br/><br/>
        {
          exams.results.map((exam) => (
        <>
        <ReactAudioPlayer
                    src={exam.Audio}
                    autoPlay
                    controls
                />
<br/><br/>

        <h3 className="question-text">{exam.Question}</h3>
                        <ul className="ul1">
                       
                <li className="li1"  onClick={() => optionClicked()}>
                {exam.Ans_1}
                
                 
                </li>
                <li className="li1"
                onClick={() => optionClicked()}
                 
                 >
                   {exam.Ans_2}
                   
                 </li>
                 <li className="li1"
                 onClick={() => optionClicked()}
                 >
                   {exam.Ans_3}
                  
                 </li>
                 <li className="li1"
                 onClick={() => optionClicked()}
                 >
                  {exam.Ans_4}
                  
                 </li>
               

                           
        </ul>
        </>
          ))}
                
<br/>


{auth.status=== 1 && (
                  <>
                          <div className="btn1" style={{backgroundColor:"rgb(248, 243, 243)"}}>
        <Button name={'Edit'} go_to={'/edit'}/>
        <Button name={'Delete'} go_to={''}/>
        
</div>
          </>
                )}
     </div>
        
    
    </div>
        </>

    );

    
}
